<?phpfunction consultaUsuario($data, $client){	require ("lib/bd.php");		$id = $data->id;	if($id == ""){		return false;	}	$cpf = $data->user;	if($cpf == ""){		return false;	}	$senha = $data->password;	if($senha == ""){		return false;	}		echo "CLIENTE:" . $client . "\n";	$sql = "";	$consulta;	if(is_null($id)){		echo "Verificando login e senha\n";		$sql = "SELECT * FROM usuario WHERE cpf = ? and senha = ? and cliente = ?"; //FAZER CORREÇÃO PARA MAIS CLIENTES		$consulta = $bd->prepare($sql);		$consulta->bindParam(1, $cpf);		$consulta->bindParam(2, $senha);		$consulta->bindParam(3, $client);	}else{		echo "Verificando informacoes do token\n";		$sql = "SELECT * FROM usuario WHERE id = ? and cliente = ?"; //FAZER CORREÇÃO PARA MAIS CLIENTES		$consulta = $bd->prepare($sql);		$consulta->bindParam(1, $id);		$consulta->bindParam(2, $client);	}		$consulta->execute();		$usuarios;	while($row = $consulta->fetch(PDO::FETCH_OBJ)){		$usuarios = array("id" => $row->id, "client" => $row->cliente,"name" => $row->nome, "cpf"=>$row->cpf, "email"=>$row->email, "phone"=>$row->telefone, "img"=>$row->img);		echo $row->id . " - " . $row->cliente . " - " . $row->nome . " - " . $row->cpf . " - " . $row->email . " - " . $row->telefone . "\n";		    }		if (is_array($usuarios)) {	    return $usuarios;	} else {		echo "Nenhum registro encontrado\n";		return false;	}			}function cadastraUsuario($data, $client){	require ("lib/bd.php");		$cliente = $client;	$nome = $data->name;	if(count(explode(" ", $nome)) == 1){		return "-2";	}	$cpf = $data->cpf;	if(strlen($cpf) < 11){		return "-3";	}	$email = $data->email;	if(count(explode("@", $email)) == 1){		return "-4";	}	$telefone = $data->phone;	if(strlen($telefone) < 14){		return "-5";	}	$senha = $data->password;	if($senha == ""){		return "-6";	}		$chave = mt_rand(1000,9999);		echo "Inserindo registro usuario:\n";	$sql = "INSERT INTO usuario (cliente, nome, cpf, senha, email, telefone, chave, ativo) VALUES (?, ?, ?, ?, ?, ?, ?, ?)"; //FAZER CORREÇÃO PARA MAIS CLIENTES	$consulta = $bd->prepare($sql);	$consulta->bindValue(1, $cliente);	$consulta->bindValue(2, $nome);	$consulta->bindValue(3, $cpf);	$consulta->bindValue(4, $senha);	$consulta->bindValue(5, $email);	$consulta->bindValue(6, $telefone);	$consulta->bindValue(7, $chave);	$consulta->bindValue(8, false);	$consulta->execute();	if($consulta){		if(envia_email("Sistema de confirmação", $email, "Código de confirmação de cadastro", "Seja bem vindo " . $nome . "\n\n" . "Este é seu código de verificação: " . $chave)){			echo "E-mail de confirmacao enviado com sucesso\n";			return $bd->lastInsertId();		}else{			return "-1";		}	}else{		return "0";		//return "failed"; //NA VERIFICAÇÃO SE OS DADOS VIERAM CORRETOS, CASO NÃO TENHAM VINDO DEVE-SE RETORNAR ERROR, POR ISSO NÃO É TRUE E FALSE	}			}function validaUsuario($data, $client, $user){ //FAZER CÓDIGO QUE VERIFIQUE SE OS DADOS VIERAM CORRETOS	require ("lib/bd.php");	$chave = $data->cod;	echo "Validando usuario: $id\n";	$sql = "UPDATE usuario SET ativo = 1 WHERE chave = ? and id = ?"; //FAZER CORREÇÃO PARA MAIS CLIENTES	$consulta = $bd->prepare($sql);	$consulta->bindParam(1, $chave);	$consulta->bindParam(2, $user);	$consulta->execute();	if($consulta->rowCount()){		return "success"; //NA VERIFICAÇÃO SE OS DADOS VIERAM CORRETOS, CASO NÃO TENHAM VINDO DEVE-SE RETORNAR ERROR, POR ISSO NÃO É TRUE E FALSE	}else{		return "failed";	}}function validaCadastroUsuario($data, $client){	require ("lib/bd.php");	$cpf = $data->cpf;		echo "Verificando usuario pelo cpf\n";	$sql = "SELECT * FROM usuario WHERE cpf = ? and cliente = ?"; //FAZER CORREÇÃO PARA MAIS CLIENTES	$consulta = $bd->prepare($sql);	$consulta->bindParam(1, $cpf);	$consulta->bindParam(2, $client);	$consulta->execute();	$encontrado = false;	while($row = $consulta->fetch(PDO::FETCH_OBJ)){		echo "ENCONTRADO" . "\n";		$encontrado = true;	}	if($encontrado){		return false;	}else{		return true;	}			}// FUNÇÕES ESQUECI A SENHAfunction consultaUsuarioCpf($data, $client){	require ("lib/bd.php");	$cpf = $data->cpf;	echo "Verificando usuario pelo cpf\n";	$sql = "SELECT * FROM usuario WHERE cpf = ? and cliente = ?"; //FAZER CORREÇÃO PARA MAIS CLIENTES	$consulta = $bd->prepare($sql);	$consulta->bindParam(1, $cpf);	$consulta->bindParam(2, $client);		$consulta->execute();	$usuarios;	while($row = $consulta->fetch(PDO::FETCH_OBJ)){		$usuarios = array("id" => $row->id, "client" => $row->cliente,"name" => $row->nome, "cpf"=>$row->cpf, "email"=>$row->email, "phone"=>$row->telefone, "img"=>$row->img);		echo $row->id . " - " . $row->cliente . " - " . $row->nome . " - " . $row->cpf . " - " . $row->email . " - " . $row->telefone . "\n";	}	if (is_array($usuarios)) {		return $usuarios;	} else {		echo "Nenhum registro encontrado\n";		return false;	}			}function gerarNovaChave($client, $user){ //FAZER CÓDIGO QUE VERIFIQUE SE OS DADOS VIERAM CORRETOS	require ("lib/bd.php");	$chave = mt_rand(1000,9999);	$dados = consultaUsuario(json_decode(json_encode(array("id"=>$user))), $client);	$email = $dados["email"];	echo "Gerando nova chave de usuario\n";	$sql = "UPDATE usuario SET chave = ? WHERE cliente = ? and id = ?"; //FAZER CORREÇÃO PARA MAIS CLIENTES	$consulta = $bd->prepare($sql);	$consulta->bindParam(1, $chave);	$consulta->bindParam(2, $client);	$consulta->bindParam(3, $user);	$consulta->execute();	if($consulta->rowCount()){		if(envia_email("Recuperação de senha", $email, "Código de recuperação de senha", "Olá " . $nome . "\n\n" . "Você solicitou a recuperação de sua senha em " . date('H:i:s d-m-Y') . "\n" . "Este é seu código: " . $chave . "\n" . "Informe ele no aplicativo para poder cadastrar uma nova senha.")){			return array("success", $email); //NA VERIFICAÇÃO SE OS DADOS VIERAM CORRETOS, CASO NÃO TENHAM VINDO DEVE-SE RETORNAR ERROR, POR ISSO NÃO É TRUE E FALSE		}else{			return array("failed");		}	}else{		return array("failed");	}}function gravarSenha($data, $client, $cpf){ //FAZER CÓDIGO QUE VERIFIQUE SE OS DADOS VIERAM CORRETOS	require ("lib/bd.php");		$senha = $data->password;	$chave = $data->cod;	echo "Gravando nova senha de usuario\n";	$sql = "UPDATE usuario SET senha = ? WHERE cliente = ? and cpf = ? and chave = ?"; //FAZER CORREÇÃO PARA MAIS CLIENTES	$consulta = $bd->prepare($sql);	$consulta->bindParam(1, $senha);	$consulta->bindParam(2, $client);	$consulta->bindParam(3, $cpf);	$consulta->bindParam(4, $chave);	$consulta->execute();	if($consulta->rowCount()){		return "success"; //NA VERIFICAÇÃO SE OS DADOS VIERAM CORRETOS, CASO NÃO TENHAM VINDO DEVE-SE RETORNAR ERROR, POR ISSO NÃO É TRUE E FALSE	}else{		return "failed";	}}function alterarDadosUsuario($data, $client, $usuario){ //FAZER CÓDIGO QUE VERIFIQUE SE OS DADOS VIERAM CORRETOS	require ("lib/bd.php");	$cpf = $data->cpf;	$email = $data->email;	$nome = $data->name;	$telefone = $data->phone;		echo "Gravando novos dados de usuario\n";	$sql = "UPDATE usuario SET cpf = ?, email = ?, nome = ?, telefone = ? WHERE id = ?"; //FAZER CORREÇÃO PARA MAIS CLIENTES	$consulta = $bd->prepare($sql);	$consulta->bindParam(1, $cpf);	$consulta->bindParam(2, $email);	$consulta->bindParam(3, $nome);	$consulta->bindParam(4, $telefone);	$consulta->bindParam(5, $usuario);	$consulta->execute();	if($consulta->rowCount()){		return "success"; //NA VERIFICAÇÃO SE OS DADOS VIERAM CORRETOS, CASO NÃO TENHAM VINDO DEVE-SE RETORNAR ERROR, POR ISSO NÃO É TRUE E FALSE	}else{		return "failed";	}}?>